import React from "react";

function App() {
  const jobs = [
    {
      id: 1,
      title: "React UI Developer",
      company: "Tech Solutions",
      location: "Bangalore",
      salary: "₹4 - 6 LPA",
    },
    {
      id: 2,
      title: "Frontend Developer",
      company: "Startup Hub",
      location: "Hyderabad",
      salary: "₹3 - 5 LPA",
    },
    {
      id: 3,
      title: "UI Developer (Fresher)",
      company: "Innovatech",
      location: "Chennai",
      salary: "₹3 - 4 LPA",
    },
  ];

  return (
    <div>
      {/* Navbar */}
      <nav style={styles.nav}>
        <h2>Job Portal</h2>
        <div>
          <button style={styles.navBtn}>Home</button>
          <button style={styles.navBtn}>Login</button>
          <button style={styles.navBtn}>Admin</button>
        </div>
      </nav>

      {/* Title */}
      <h1 style={{ textAlign: "center", marginTop: "20px" }}>
        Latest Job Openings
      </h1>

      {/* Job Cards */}
      <div style={styles.container}>
        {jobs.map((job) => (
          <div key={job.id} style={styles.card}>
            <h3>{job.title}</h3>
            <p>
              <b>Company:</b> {job.company}
            </p>
            <p>
              <b>Location:</b> {job.location}
            </p>
            <p>
              <b>Salary:</b> {job.salary}
            </p>
            <button style={styles.applyBtn}>Apply Now</button>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  nav: {
    display: "flex",
    justifyContent: "space-between",
    padding: "15px 30px",
    backgroundColor: "#0f172a",
    color: "white",
  },
  navBtn: {
    marginLeft: "10px",
    padding: "8px 15px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  container: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    marginTop: "20px",
  },
  card: {
    width: "300px",
    border: "1px solid #ddd",
    borderRadius: "10px",
    padding: "20px",
    margin: "15px",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
    backgroundColor: "white",
  },
  applyBtn: {
    backgroundColor: "#2563eb",
    color: "white",
    padding: "8px 15px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default App;
