function JobCard({ title, company, location, salary }) {
  return (
    <div style={styles.card}>
      <h3>{title}</h3>
      <p>
        <b>Company:</b> {company}
      </p>
      <p>
        <b>Location:</b> {location}
      </p>
      <p>
        <b>Salary:</b> {salary}
      </p>
      <button style={styles.button}>Apply Now</button>
    </div>
  );
}

const styles = {
  card: {
    border: "1px solid #ddd",
    borderRadius: "10px",
    padding: "20px",
    margin: "15px",
    width: "300px",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
  },
  button: {
    background: "#2563eb",
    color: "white",
    padding: "8px 15px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default JobCard;
