import React, { useEffect, useState } from "react";

function Timer() {
  const [time, setTime] = useState(30); // change to 120 for 2 minutes
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    if (!isRunning || time === 0) return;

    const interval = setInterval(() => {
      setTime((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(interval);
  }, [isRunning, time]);

  return (
    <div>
      <h2>Time Left: {time} seconds</h2>

      <button onClick={() => setIsRunning(true)}>Start</button>
      <button onClick={() => setIsRunning(false)}>Pause</button>
      <button onClick={() => setTime(30)}>Set 30s</button>
      <button onClick={() => setTime(120)}>Set 2 min</button>
      <button onClick={() => setTime(0)}>Stop</button>
    </div>
  );
}

export default Timer;
